const { computeTimeline } = require('./campaign/aggregator.js');
const fs = require('fs');

// Load the scenario data
const scenario = JSON.parse(fs.readFileSync('./data/scenario.json', 'utf8'));

// Test the implementation
console.log('Testing computeTimeline with scenario data...\n');

const result = computeTimeline(scenario);

// Display results in a readable format
for (const day of result) {
  console.log(`Date: ${day.date}`);
  for (const [flightId, flight] of Object.entries(day.flights)) {
    console.log(`  ${flightId}: status=${flight.status}, budget=${flight.budget}`);
  }
  console.log();
}

// Also output as JSON for validation
